import './App.css';
import React,{useState} from 'react'
function App() {

  const {weight,setWeight} = useState(0);
  const {height,setHeight} = useState(0);
  const {bmi,setBMI} = useState(0);
  const {message,setMessage} = useState('');


  let calcBMI = (e) => {
   e.preventDefault();
    if(weight === 0 || height === 0){
      alert('Insira um valor válido de altura e largura')
    }




    else{
      let bmi = (weight/(height * height)*10000)
      setBMI(bmi.toFixed(1));
      if(bmi<25){
        setMessage('Você vai morrer assim muito magro')
      }
      else if(bmi > 25){
        setMessage('Aí sim Fit');
      }
      else if(bmi <= 25 && bmi < 30){
        setMessage('Projetinho Felas')
      }
      else if (bmi =>30){
        setMessage('Em pleno século 20, ano da copa, você conhece o elon musk')
      }
      else{
        setMessage('Vai morrer maluco');
      }
    }
  }


let reload = () => {
  window.loaction.reload()
}

  return (
    <div className='App'>
      <div className='container'>
      <h2>BMI APP</h2>
      <form onSubmit={calcBMI}>
        <div>
          <label>Weight(KG)</label>
          <input type="text" placeholder='Coloque sua Massa aqui' value={weight} onChange={(e) => setWeight(e.target.value)} />
        </div>
        <div>
          <label>Height</label>
          <input type="text" placeholder='Coloque sua Altura aqui' value={height} onChange={(e) => setHeight(e.target.value)}  />
        </div>
        <div>
          <button className='btn' type='submit'>Enviar</button>
          <button className='btn btn-outline' onClick={reload} type="submit">Reload</button>
        </div>
        <div className='center'>
          <h3>Your BMI is:{bmi}</h3>
          <p>{message}</p>
        </div>
      </form>

      </div>



    </div>
  );
}

export default App;
